import React from 'react';
import unsplash from '../api/unsplash';
import FieldsViewer from './FieldsViewer';

class FormViewer extends React.Component{
    constructor(props){
        super(props);
        this.state={
            id:props.match.params.id,
            Schema:[]

        }
    }
     componentDidMount=async()=>{
        const FormSchema= await unsplash.post('/getFormSchema',{
            id:this.props.match.params.id
        }).then((response)=> {
            if(response.data.success===true){
                var schema=JSON.parse(response.data.data[0].formSchema);
                this.setState({Schema:schema});
                console.log( this.state.Schema);
                this.g();
            }
            else
            console.log(response);
    }).catch((error)=> {
        console.log("got error while posting data", error);
     });
     console.log( this.state.Schema);
    }
    
    g=()=>{
        const result=this.state.Schema.map((item, index)=>{
            return(
            <FieldsViewer
                key={index} 
                name={item.name}
                 label={item.label}
                 type={item.type}

                    />
          
                    )})
    }
    
    render(){
         
        return(
            <div className="ui segment">
                <div >
                    <h1 className="ui header">{this.state.Schema.title}</h1>
                </div>
                <form onSubmit={this.onFormSubmit} className="ui form"> 
                {
                        
                       this.state.Schema && [result]
                }
                </form>
                        
            </div>
        );
    }
}

export default FormViewer;
