import React from 'react';
import Wizard from './Wizard';

class App extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            forms:[]
        };
      
    }
    GetFormProperties=(e)=>{  
        console.log(e);
      
        this.setState(prevState => ({
            forms: [...prevState.forms, e]}));

            console.log(this);
            this.SaveForm();
    }
    SaveForm=()=>
    {
        //לשנות אייפי
        fetch('http://127.0.0.1:5000/AddForm',{
          method:'POST',
          headers:{
              'Accept':'application/json',
              'Content-Type':'application/json',
          },
          body: JSON.stringify({
            forms:this.state.forms 
          })
        })
          .then((response)=>response.json())
          .then((res)=>
          {
              if(res.success===true)
              {
                  alert('is added to the system!')   ;  
              }
              else
              {
                  alert(res.message);
              }
          });
      }
  
    render(){
        return(
            <div className="ui container" style={{marginTop:'10px'}}>
                <Wizard GetForm={this.GetFormProperties}/>
                </div>
        );
    }

}


export default App;
