import React from 'react';
import unsplash from '../api/unsplash';
import FieldsViewer from './FieldsViewer';

class FormViewer extends React.Component{
    constructor(props){
        super(props);
        this.state={
            id:props.match.params.id,
            Schema:[]

        }
    }
     componentDidMount=async()=>{

        const FormSchema=await unsplash.post('/getFormSchema',{
            id:this.props.match.params.id
        });

        var schema=JSON.parse(FormSchema.data[0].formSchema);

        this.setState({Schema:schema});

        console.log( this.state.Schema);
    }
    

     g=()=>{
         
        const result = this.state.Schema.properties.map((field, index) =>{
            return(
                <FieldsViewer
                    key={index} 
                    name={field.name}
                    label={field.label}
                    type={field.type}
                    
                />
            )
        })
         
    }
     

    render(){
       
   console.log(this.state.Schema.properties);
   
        return(
            <div className="ui segment">
                <div >
                    <h1 className="ui header">{this.state.Schema.title}</h1>
                </div>
                <form onSubmit={this.onFormSubmit} className="ui form"> 
                {
                        
                    //    this.state.Schema && <FieldsViewer
                    //                             name={this.state.Schema.properties}
                    //                             // label={this.state.Schema.properties.label}
                    //                             // type={this.state.Schema.properties.type}
                       
                    //                              />
                }
                </form>
                        
            </div>
        );
    }
}

export default FormViewer;
