import React from 'react';
import WizardField from './WizardField';
import DropDownWizardField from './DropDownWizardField';

 class PropertyCard extends React.Component{
 
    constructor(props){
        super(props);
        this.state={
            label:'',
            name:'',
            type:'',  
        };
        this.ChangeName = this.ChangeName.bind(this);
        this.ChangeLabel = this.ChangeLabel.bind(this);
    }
    
    ChangeName(updatedName){
        this.setState(prevState => {
            return {
                name: updatedName
            };
          });
        }
    

    ChangeLabel = (updatedName) => {
        console.log("updatedName: "+updatedName);
        this.setState({label: updatedName});
        console.log(this.state.label);
    }

    ChangeType = (updatedName) => {
        this.setState({type: updatedName});
        console.log(this.state.type);
    }

    CreateJson = () => {
      
        console.log(JSON.stringify(this.state));


    }

  
    render(){
        return(
           
            <div>
                <WizardField 
                label={'label'}
                type={'text'}
                name={this.state.label} 
                onUpdate={this} 
                handleChange={this.ChangeLabel}
                />

                <WizardField 
                label={'name'}
                type={'text'}
                name={this.state.name} 
                onUpdate={this} 
                handleChange={this.ChangeName}/>

                <DropDownWizardField 
                label={'type'}
                name={this.state.type} 
                onUpdate={this} 
                handleChange={this.ChangeType}/>

                <button 
                style={{marginTop:"10px" }}
                className="ui primary button"
                variant="primary"
                onClick={this.CreateJson}
                >Done!</button>
            </div>

        );
    }
}


export default PropertyCard;
