import React from 'react'
import { Dropdown } from 'semantic-ui-react'

const typeOptions = [
  { key: 'text', value: 'text', text: 'text' },
  { key: 'color', value: 'color', text: 'color' },
  { key: 'date', value: 'date', text: 'date' },
  { key: 'email', value: 'email', text: 'email' },
  { key: 'tel', value: 'tel', text: 'tel' },
  { key: 'number', value: 'number', text: 'number' },

]
class DropDownWizardField extends React.Component{
    constructor(props){
        super(props);
        this.state={
            name:'',
        }
    }

    handleChange = (e) => {
        this.setState({ name: e.target.value });
        this.props.handleChange(this.state.name);
      }

    componentDidMount() {
        this.setState({ name: this.props.name });
      }

    render(){
        return(
            <div                  style={{marginTop:"15px" }}
            >
                <label>
                    {this.props.label}:
                </label>

                <Dropdown 
                clearable 
                options={typeOptions} 
                selection
                type={this.props.type}
                value={this.state.name} 
                onChange={this.handleChange}
                />
            </div>
            );
    }
}

export default DropDownWizardField;
