import React from 'react';
import PropertyCard from './PropertyCard';
class Wizard extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            showCard:false,
            title:'',
            field:{
                type:'',
                name:'',
                label:''
            },
            fields:[]
                
            
        };
    }

    CreateProperty=()=>{
        this.setState({showCard:true});
    }
    onFormSubmit=(e)=>{
        //prevent the page refreshing every 'enter' press 
        e.preventDefault();
 
    }
    AddField=(e)=>{
        console.log(e);

        const {field}={...this.state};
        const {CurrentField}=field;
        const{name,value}=e;
        CurrentField[name]=value;
        
        this.setState({field:CurrentField});
        console.log(this.state.field);

}

    render(){
        return(
            <div className="ui segment">
            <form onSubmit={this.onFormSubmit} className="ui form"> 
                <div className="field">
                    <input 
                        className="ui header"
                        placeholder="Place your form title here"
                        type="text" 
                        value={this.state.title } 
                        onChange={(e)=>this.setState({title:e.target.value})}
                    />

                    <div>
                        <button 
                        style={{marginTop:"50px" }}
                        className="ui button"
                        onClick={this.CreateProperty}>
                            Add Field
                        </button>
                    </div>

                    {
                        this.state.showCard && <PropertyCard GetPropertyJson={this.AddField}/>
                    }
                </div>
            </form>
        </div>
        );
    }

}


export default Wizard;
