import React from 'react';
import unsplash from '../api/unsplash';
import FieldsViewer from './FieldsViewer';
var result;
class FormViewer extends React.Component{
    constructor(props){
        super(props);
        this.state={
            id:props.match.params.id,
            Schema:[],
            flag:false,
            formData:[]

        }

    }
     componentDidMount=async()=>{
        const FormSchema= await unsplash.post('/getFormSchema',{
            id:this.props.match.params.id
        }).then((response)=> {
            if(response.data.success===true){
                var schema=JSON.parse(response.data.data[0].formSchema);
                this.setState({Schema:schema});
                this.setState({flag:true});
            }
            else
            console.log(response);
    }).catch((error)=> {
        console.log("got error while posting data", error);
     });
     console.log(this.state.Schema);
    }
    
    onFormSubmit=(e)=>{
        //prevent the page refreshing every 'enter' press 
        e.preventDefault();
        
 
    }
    SaveForm=(e)=>
    {
        unsplash.post("/SaveData",{
            data: this.state.formData,
            id:this.state.id
        }).then((response)=> {
            if(response.success)
                console.log("Data submitted successfully: ", response);
                else
                console.log("NO SUCESS! ",response);
        }).catch((error)=> {
           console.log("got errr while posting data", error);
        });
    }
    ChangeVal=(name,e)=>{
        console.log(name);
        const fields={...this.state.formData}
        console.log(fields);
        fields[name]= e.target.value;
        this.setState({formData:fields});
    }
    render(){  
        if(this.state.flag){
            result=this.state.Schema.properties.map((item, index)=>{

                return(
                <FieldsViewer
                    key={index} 
                    label={item.label}
                    type={item.type}
                    handleChange={this.ChangeVal.bind(this, item.name)}

                        />
            
                        )})
            }
        return(
            <div className="ui segment">
                <div >
                    <h1 className="ui header">{this.state.Schema.title}</h1>
                </div>
                <form onSubmit={this.onFormSubmit}  noValidate className="ui form"> 
                {     
                      this.state.flag && [result]
                }
                 <button 
                    type='submit'
                    style={{marginTop:"50px" }}
                    className="ui button"
                    onClick={this.SaveForm}>
                       Done!
                    </button>
                </form>
                        
            </div>
        );
    }
}

export default FormViewer;
