    render(){
        console.log(this.state.name);

        return(
            <div>
                <label>
                    {this.props.label}:
                </label>
                <input 
                    type={this.props.type}
                    value={this.state.name} 
                    onChange={this.handleChange_}
                />
            </div>
            );
    }
}

export default WizardField;
