import React from 'react';
import PropertyCard from './PropertyCard';
import FieldsViewer from './FieldsViewer';

class Wizard extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            showCard:false,
            title:'',
            type:'',
            name:'',
            label:'',
            fields:[]
            
            
        };
        this.AddField = this.AddField.bind(this);
        this.CreateProperty = this.CreateProperty.bind(this);

    }

    CreateProperty=()=>{
        this.setState({showCard:true});
    }
    onFormSubmit=(e)=>{
        //prevent the page refreshing every 'enter' press 
        e.preventDefault();
 
    }
    AddField=(e)=>{
        var prevState = JSON.parse(e);
        this.setState({name:prevState['name']});
        this.setState({label:prevState['label']});
        this.setState({type:prevState['type']});
        this.setState({showCard:false}); 
        // console.log(this);  
        this.pushFieldToFields();
    }

    pushFieldToFields=()=>{
        this.setState(prevState => ({
            fields: [...prevState.fields, {name:prevState.name,
                label:prevState.label,
                type:prevState.type}]
          }))

          //console.log(this);  
    }

    render(){
        const result = Object.keys(this.state.fields).map((field, index) =>{
            return(
                <FieldsViewer
                    key={index} 
                    field={field}
                    fields={this.state.fields[field]} 
                />
            )
            })
        return(
            <div className="ui segment">
            <form onSubmit={this.onFormSubmit} className="ui form"> 
                <div className="field">
                    <input 
                        className="ui header"
                        placeholder="Place your form title here"
                        type="text" 
                        value={this.state.title } 
                        onChange={(e)=>this.setState({title:e.target.value})}
                    />

                    <div>

                    <button 
                    style={{marginTop:"50px" }}
                    className="ui button"
                    onClick={this.CreateProperty}>
                        Add Field
                    </button>

                    </div>

                    {
                        this.state.showCard && <PropertyCard GetPropertyJson={this.AddField}/>
                    }

                  
                    {
                        
                        this.state.fields.length>0 && {result}
                    }
                       
                    </div>
            </form>
        </div>
        );
    }

}


export default Wizard;
