import React from 'react';
import unsplash from '../api/unsplash';
import { async } from 'q';

class DataViewer extends React.Component{
    constructor(props){
        super(props);
        this.state={
            headers:[],
            flag:false,
            data:[]
        }
    }
    nextPath=(path)=>{
        this.props.history.push(path);
    }

    componentDidMount=async()=>{
        this.getFormSchema();
        this.getFormData();
        
    }

    getFormData=async()=>{
        await unsplash.post('/getFormData',{
            id:this.props.match.params.id
        }).then((response)=> {
            if(response.data.length>0){

                for(var i=0;i<response.data.length;i++)
                {
                    var  formObj=JSON.parse(response.data[i].data)

                    this.setState(prevState => ({
                        data: [...prevState.data,formObj]
                    }))
                }
 
            }
            else{
               

            }
            console.log(response);
    }).catch((error)=> {
        console.log("got error while posting data", error);
     });
     console.log(this.state);
    }
    getFormSchema=async()=>{
        await unsplash.post('/getFormSchema',{
            id:this.props.match.params.id
        }).then((response)=> {
            if(response.data.success===true){
                var schema=JSON.parse(response.data.data[0].formSchema);
                this.setState({headers:schema.properties});
                this.setState({flag:true});
              
            }
            else
            console.log(response);
    }).catch((error)=> {
        console.log("got error while posting data", error);
     });
     console.log(this.state);
    }

    renderTableHeader() {
        if(this.state.flag){
            let header = Object.keys(this.state.headers[0])
            return header.map((key, index) => {
               return <th key={index}>{key.toUpperCase()}</th>
            })
        }
    
     }
    render(){
        return(
            <div className="ui container" style={{marginTop:'10px'}}>
            <table className="ui celled table" id='FormData'>
               <tbody>
               <tr>{this.renderTableHeader()}</tr>
               </tbody>
            </table>
                 <button 
                style={{marginTop:"50px" }}
                className="ui button"
                onClick={()=>this.nextPath('/')}>
                    Back
            </button> 
            </div>
        );
    }
}

export default DataViewer;
